		function getConfig(aString){
			switch(aString){
				case "ScriptStyle":{return "2018a";}
				case "ShowEnergyCache":{return 1;}
				case "ShowTouchDamage":{return 1;}
				case "ShowLiquidCache":{return 1;}
				case "EnableTemperatureDisplay":{return 1;}
				case "EnableTemperatureLimit":{return 0;}
				case "CrucibleMaxShowTemp":{return 1800;}
			}
		}



