	function getMaterialName(aID){
		var t = Packages.gregapi.oredict.OreDictMaterial.get(aID);
		return translate("gt.material." + t.mNameInternal);
	}



	function getInternalODName(aID){
		var t = Packages.gregapi.oredict.OreDictMaterial.get(aID);
		return t.mNameInternal;
	}



