        function Mold2Recipes(mold){
			var Prefix = getWorld().func_147438_o(mold['x'],mold['y'],mold['z']).getMoldRecipe(nbt['gt.mold']).toString();
            switch(Prefix){
                case "bolt":{return "螺栓";}
                case "casingSmall":{return "外壳";}
                case "chunkGt":{return "小块";}
                case "gearGt":{return "齿轮";}
